SELECT count(t.id) AS loginCount, t.app_code AS appCode, max(ta. NAME) appName FROM t_sa_admin_app t LEFT JOIN t_app ta ON t.app_code = ta. CODE AND ta.delete_status = 0 WHERE t.delete_status = 0 and t.login_status = 1
and t.app_code = #{appCode}
GROUP BY t.app_code
