mapper.select:
select * from t_sa_dict_category t

    1=1

        and t.id = #{id}


        and t.name = #{name}

    and t.delete_status = 0

select t.* from (
    SELECT t.*,ds2.appNames FROM t_sa_dict_category t LEFT JOIN (
        SELECT ds.id,GROUP_CONCAT(ds.appName) AS appNames FROM (
            SELECT t.*,ta.name AS appName FROM t_sa_dict_category t
            LEFT JOIN t_sa_dict_category_app tsdca ON t.id = tsdca.dict_category_id AND tsdca.delete_status=0
            LEFT JOIN t_app ta ON tsdca.app_code = ta.code AND ta.delete_status = 0
            WHERE t.delete_status = 0

                and ta.code = #{appCode}

        )ds
        GROUP BY ds.id
    )ds2 ON t.id = ds2.id
)t

    1=1


    and t.delete_status = 0

order by t.create_date desc
limit #{start},#{limit}

select count(*) from (
    select t.* from (
        SELECT t.*,ds2.appNames FROM t_sa_dict_category t LEFT JOIN (
            SELECT ds.id,GROUP_CONCAT(ds.appName) AS appNames FROM (
                SELECT t.*,ta.name AS appName FROM t_sa_dict_category t
                LEFT JOIN t_sa_dict_category_app tsdca ON t.id = tsdca.dict_category_id AND tsdca.delete_status=0
                LEFT JOIN t_app ta ON tsdca.app_code = ta.code AND ta.delete_status = 0
                WHERE t.delete_status = 0

                        and ta.code = #{appCode}

            )ds
            GROUP BY ds.id
        )ds2 ON t.id = ds2.id
    )t

        1=1


        and t.delete_status = 0

)ds

select t.*,tsoa.app_code from t_sa_dict_category t
   left join t_sa_dict_category_app tsoa on t.dict_category_id = tsoa.dict_category_id
   where  1=1 and t.id = #{id} and t.delete_status = 0 and tsoa.app_code=#{appCode}

select * from t_sa_dict_category t left join t_sa_dict_category_app tso on t.dict_category_id = tso.dict_category_id and tso.app_code in
#{item}
and tso.delete_status = 0 where 1=1 and t.delete_status = 0 and tso.app_code is not null
